import React, { Component } from "react";
import { Card, Table, Form, Button } from "react-bootstrap";

class Book extends Component {
  state = {
    title: "title1",
    author: "",
    coverUrl: "",
    isbn: "",
    price: "",
    language: "",
  };

  //when i submit the form
  handleSubmit = (e) => {
    e.preventDefault();
    alert("title: ", this.state.title, 
    "author: ", this.state.author, 
    "coverUrl: ", this.state.coverUrl,
    );
  };

  //when the values change
  handleChange = (e) => {
    //console.log(e.target.name, " ", e.target.value);
    this.setState({[e.target.name]: e.target.value});
  };

  render() {
    return (
      <Card className="rounded shadow mt-5 bg-light">
        <Card.Header className="font-weight-bold text-center">
          Let's add a new Book ðŸ“š ðŸ¤—
        </Card.Header>
        <Form className={"font-weight-bold"} onSubmit={this.handleSubmit}>
          <Card.Body>
            <Form.Row className="row">
              <Form.Group controlId="formBasicEmail" className="col">
                <Form.Label>Title :</Form.Label>
                <Form.Control
                  required
                  value={this.state.title}
                  onChange={this.handleChange}
                  type="text"
                  name="title"
                  placeholder="Enter Book title"
                />
              </Form.Group>

              <Form.Group controlId="formBasicEmail" className="col">
                <Form.Label>Author :</Form.Label>
                <Form.Control
                  required
                  value={this.state.author}
                  onChange={this.handleChange}
                  type="text"
                  name="author"
                  placeholder="Enter Book Author"
                />
              </Form.Group>
            </Form.Row>
            <Form.Row className="row">
              <Form.Group controlId="formBasicEmail" className="col">
                <Form.Label>Cover Photo Url :</Form.Label>
                <Form.Control
                  required
                  value={this.state.coverUrl}
                  onChange={this.handleChange}
                  type="text"
                  name="coverUrl"
                  placeholder="cover photo"
                />
              </Form.Group>

              <Form.Group controlId="formBasicEmail" className="col">
                <Form.Label>ISBN number :</Form.Label>
                <Form.Control
                  required
                  value={this.state.isbn}
                  onChange={this.handleChange}
                  type="text"
                  name="isbn"
                  placeholder="Enter ISBN number"
                />
              </Form.Group>
            </Form.Row>
            <Form.Row className="row">
              <Form.Group controlId="formBasicEmail" className="col">
                <Form.Label>Price :</Form.Label>
                <Form.Control
                  required
                  value={this.state.price}
                  onChange={this.handleChange}
                  type="text"
                  name="price"
                  placeholder="Enter Price"
                />
              </Form.Group>

              <Form.Group controlId="formBasicEmail" className="col">
                <Form.Label>Language :</Form.Label>
                <Form.Control
                  required
                  value={this.state.language}
                  onChange={this.handleChange}
                  type="text"
                  name="language"
                  placeholder="Enter the language"
                />
              </Form.Group>
            </Form.Row>
          </Card.Body>
          <Card.Footer className="text-right">
            <Button variant="success" type="submit">
              Add
            </Button>
          </Card.Footer>
        </Form>
      </Card>
    );
  }
}

export default Book;
