import React, { Component } from "react";
import { Card, Form, Button } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSave, faPlusSquare } from "@fortawesome/free-solid-svg-icons";

class Book extends Component {
  state = {
    title: "",
    author: "",
    coverPhotoURL: "",
    isbnNumber: "",
    price: "",
    language: "",
  };

  //constructor
  constructor(){
    
  }

  //storing the initial state
  initialState = {
    title: "",
    author: "",
    coverPhotoURL: "",
    isbnNumber: "",
    price: "",
    language: "",
  }

  //when i submit the form
  handleSubmit = (e) => {
    e.preventDefault();
    console.log(
      "title: ",
      this.state.title,
      "author: ",
      this.state.author,
      "coverUrl: ",
      this.state.coverPhotoURL,
      "isbn: ",
      this.state.isbnNumber,
      "price: ",
      this.state.price,
      "language: ",
      this.state.language
    );
  };

  //when the values change
  handleChange = (e) => {
    //console.log(e.target.name, " ", e.target.value);
    this.setState({ [e.target.name]: e.target.value });
  };

  render() {
    const{title, author, coverPhotoURL, isbnNumber, price, language} = this.state;
    return (
      <Card className="rounded shadow mt-5 bg-light">
        <Card.Header className="font-weight-bold text-center">
          <FontAwesomeIcon icon={faPlusSquare} /> Let's add a new Book 📚 🤗
        </Card.Header>
        <Form className={"font-weight-bold"} onSubmit={this.handleSubmit}>
          <Card.Body>
            <Form.Row className="row">
              <Form.Group controlId="formBasicEmail" className="col">
                <Form.Label>Title :</Form.Label>
                <Form.Control
                  required
                  autoComplete="off"
                  value={title}
                  onChange={this.handleChange}
                  type="text"
                  name="title"
                  placeholder="Enter Book title"
                />
              </Form.Group>

              <Form.Group controlId="formBasicEmail" className="col">
                <Form.Label>Author :</Form.Label>
                <Form.Control
                  required
                  autoComplete="off"
                  value={author}
                  onChange={this.handleChange}
                  type="text"
                  name="author"
                  placeholder="Enter Book Author"
                />
              </Form.Group>
            </Form.Row>
            <Form.Row className="row">
              <Form.Group controlId="formBasicEmail" className="col">
                <Form.Label>Cover Photo Url :</Form.Label>
                <Form.Control
                  required
                  autoComplete="off"
                  value={coverPhotoURL}
                  onChange={this.handleChange}
                  type="text"
                  name="coverPhotoURL"
                  placeholder="cover photo"
                />
              </Form.Group>

              <Form.Group controlId="formBasicEmail" className="col">
                <Form.Label>ISBN number :</Form.Label>
                <Form.Control
                  required
                  autoComplete="off"
                  value={isbnNumber}
                  onChange={this.handleChange}
                  type="text"
                  name="isbnNumber"
                  placeholder="Enter ISBN number"
                />
              </Form.Group>
            </Form.Row>
            <Form.Row className="row">
              <Form.Group controlId="formBasicEmail" className="col">
                <Form.Label>Price :</Form.Label>
                <Form.Control
                  required
                  autoComplete="off"
                  value={price}
                  onChange={this.handleChange}
                  type="text"
                  name="price"
                  placeholder="Enter Price"
                />
              </Form.Group>

              <Form.Group controlId="formBasicEmail" className="col">
                <Form.Label>Language :</Form.Label>
                <Form.Control
                  required
                  autoComplete="off"
                  value={language}
                  onChange={this.handleChange}
                  type="text"
                  name="language"
                  placeholder="Enter the language"
                />
              </Form.Group>
            </Form.Row>
          </Card.Body>
          <Card.Footer className="text-right">
            <Button variant="success" type="submit">
              <FontAwesomeIcon icon={faSave} /> Save
            </Button>
          </Card.Footer>
        </Form>
      </Card>
    );
  }
}

export default Book;
