import React, { Component } from "react";
import Book from "./Book";
import { Card, Table, Image } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faList } from "@fortawesome/free-solid-svg-icons";
import axios from "axios";

class BookList extends Component {
  state = {
    books: [],
  };

  //this method is invoked immedietly after a component
  //is mounted(inserted into the tree)
  componentDidMount = () => {
    axios
      .get("http://localhost:8081/rest/books")
      .then((response) => response.data)
      .then((data) => {
        this.setState({
          books: data,
        });
        console.log(this.state.books);
      });
  };

  //this method is invoked immedietly after
  //updating occurs
  componentDidUpdate = () => {};

  render() {
    return (
      <Card className="bg-light shadow mt-5 rounded">
        <Card.Body>
          <Card.Header className="font-weight-bold text-center">
            <FontAwesomeIcon icon={faList} /> Here is the list of Books ðŸ“šðŸ˜ƒ
          </Card.Header>
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>Title</th>
                <th>Author</th>
                <th>ISBN Number</th>
                <th>Language</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {this.state.books.length === 0 ? (
                <tr align="center">
                  <td colSpan="6">No Books Available ðŸ‘€</td>
                </tr>
              ) : (
                this.state.books.map((book) => (
                  <tr key={book.id}>
                    <td>
                      
                      {book.title}
                    </td>
                    <td>{book.author}</td>
                    <td>{book.isbnNumber}</td>
                    <td>{book.language}</td>
                    <td>action</td>
                  </tr>
                ))
              )}
            </tbody>
          </Table>
        </Card.Body>
      </Card>
    );
  }
}

export default BookList;
